import ollama
from config.settings import MAX_SUMMARY_LENGTH
import re
        
class EmailSummarizer:
    def __init__(self):
        self.model = "mistral"  # You can also use "llama2" or other models

    def clean_email_content(self, content):
        """Clean and prepare email content for summarization"""
        # Remove HTML tags and extra whitespace
        content = re.sub(r'<[^>]+>', ' ', content)
        content = re.sub(r'\s+', ' ', content).strip()
        return content

    def summarize_email(self, subject, content):
        """Summarize email content using Ollama"""
        cleaned_content = self.clean_email_content(content)
        
        prompt = f"""Please provide a concise summary of the following newsletter:
        Subject: {subject}
        Content: {cleaned_content}
        
        Summary (max {MAX_SUMMARY_LENGTH} words):"""

        try:
            response = ollama.generate(
                model=self.model,
                prompt=prompt,
                options={
                    'temperature': 0.3,
                    'system': "You are a helpful assistant that creates concise summaries. Keep summaries under 150 words."
                }
            )
            return response['response']
        except Exception as e:
            print(f"Error summarizing email: {str(e)}")
            return None

    def generate_daily_brief(self, summaries):
        """Generate a formatted daily brief from multiple summaries"""
        if not summaries:
            return "No newsletters to summarize today."

        brief = "<h2>📰 Daily Newsletter Brief</h2>\n\n"
        
        for summary in summaries:
            brief += f"<h3>{summary['subject']}</h3>\n"
            brief += f"<p>{summary['summary']}</p>\n"
            brief += f"<small>From: {summary['sender']}</small>\n"
            brief += "<hr>\n\n"

        return brief 
    
